// CardDisplay.js
import React from "react";
import "./CardDisplay.css"; // Import CSS file for card display styles

const CardDisplay = ({ currentCard, previousCard }) => {
  return (
    <div className="card-display">
      <div className="placeholder-card"></div>
      {currentCard && (
        <img
          src={currentCard.image}
          alt="Current Card"
          className="card-image"
        />
      )}

      <div className="placeholder-card"></div>
      {previousCard && (
        <img
          src={previousCard.image}
          alt="Previous Card"
          className="card-image"
        />
      )}
    </div>
  );
};

export default CardDisplay;
