// App.js
import React, { useState, useEffect } from "react";
import CardDisplay from "./components/CardDisplay";
import Button from "./components/Button";
import "./App.css";

function App() {
  const [deckId, setDeckId] = useState(null);
  const [currentCard, setCurrentCard] = useState(null);
  const [previousCard, setPreviousCard] = useState(null);
  const [valueMatches, setValueMatches] = useState(0);
  const [suitMatches, setSuitMatches] = useState(0);
  const [remainingCards, setRemainingCards] = useState(52);
  const [message, setMessage] = useState("");

  useEffect(() => {
    fetchNewDeck();
  }, []);

  const fetchNewDeck = async () => {
    const response = await fetch(
      "https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1"
    );
    const data = await response.json();
    setDeckId(data.deck_id);
  };

  const drawCard = async () => {
    const response = await fetch(
      `https://deckofcardsapi.com/api/deck/${deckId}/draw/?count=1`
    );
    const data = await response.json();
    const drawnCard = data.cards[0];

    if (currentCard) {
      setPreviousCard(currentCard);
    }

    setCurrentCard(drawnCard);
    setRemainingCards(data.remaining);

    if (data.remaining === 0) {
      setCurrentCard(null);
    }
  };

  useEffect(() => {
    if (currentCard?.suit === previousCard?.suit && currentCard) {
      setMessage("Matching Suit");
      setSuitMatches((prevValue) => prevValue + 1);
    } else if (currentCard?.value === previousCard?.value && currentCard) {
      setMessage("Matching Values");
      setValueMatches((prevValue) => prevValue + 1);
    } else {
      setMessage("");
    }
  }, [currentCard]);

  useEffect(() => {
    if (remainingCards === 0) {
      setMessage(
        `Total Matching Values: ${valueMatches}, Total Matching Suits: ${suitMatches}`
      );
    }
  }, [remainingCards]);

  return (
    <div className="App">
      <h1>Deck of Cards</h1>
      {remainingCards > 0 ? (
        <Button onClick={drawCard} disabled={remainingCards === 0} />
      ) : null}
      <CardDisplay currentCard={currentCard} previousCard={previousCard} />
      <p>{message}</p>
      <p>TOTAL SUIT MATCHES{suitMatches}</p>
      <p>TOTAL VALUE MATCHES{valueMatches}</p>

      <p>Remaining cards: {remainingCards}</p>
      {/* {valueMatches > 0 && <p>SNAP VALUE!</p>} */}
    </div>
  );
}

export default App;
